import { Component, OnInit } from '@angular/core';
import { PageEvent } from '@angular/material/paginator';
import { UserService } from '../../service/user.service';
import { ConfirmUserComponent } from '../confirm-user/confirm-user/confirm-user.component';
import { MAT_DIALOG_DATA, MatDialog, MatDialogRef } from '@angular/material/dialog';
@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.scss']
})
export class UsersComponent implements OnInit {
  tableUsers:any;
  Users:any[]=[]
  length=20;
  pageSize=5;
  pageIndex=0;
  pageNumber=1;
  pageSizeOptions=[5,10,20];
  pageEvent:PageEvent|any;
  searchKey:string='';
  constructor(public dialog: MatDialog,private _UserService:UserService){}
  ngOnInit(): void {
    this.getAllUsers()
  }
getAllUsers(){
  let params={
    pageSize:this.pageSize,
    pageNumber:this.pageNumber,
    userName:this.searchKey
  }
  this._UserService.getUsers(params).subscribe({
    next:(res)=>{
      console.log(res)
      this.tableUsers=res;
      this.Users=res.data
    },
    error:(err)=>{
      console.log(err)
    },
    complete:()=>{
      //tostar
    }
  })
}
openDialog(dataItem:any) {
  const dialogRef = this.dialog.open(ConfirmUserComponent, {
    data:dataItem
  });
  dialogRef.afterClosed().subscribe(result => {
    console.log(`Dialog result: ${result}`);
    if(result){
      this.onToggleBlockUser(result)
    }
}
  )
}
onToggleBlockUser(id:number){
this._UserService.onToggleActivaUser(id).subscribe({
  next:(res)=>{
    console.log(res)
  },
  error:(err)=>{
    console.log(err)
  },
  complete:()=>{
    this.getAllUsers()
  }
})

}
handlePageEvent(e: PageEvent) {
  this.pageEvent = e;
  this.length = e.length;
  this.pageSize = e.pageSize;
  this.pageIndex = e.pageIndex;
  this.getAllUsers()
}
}

<section>
    <div class="title shadow-sm px-5 p-3 mt-4">
        <h3>Users</h3>
    </div>
</section>
<div class="usersTable">
    <div class="col-3 mt-4 ms-2">
        <div class="input-group mb-3">
         
          <input type="text" [(ngModel)]="searchKey" (keyup.enter)="getAllUsers()" class="form-control position-relative" placeholder="Search by name" aria-label="Username" aria-describedby="basic-addon1" >
         </div>
      </div>
<table class="table  shadow p-3 mb-5 bg-body-tertiary rounded-3">
    <thead>
        <tr>
            <th class="col">UserName</th>
            <th class="col">Status</th>
            <th class="col">PhoneNumber</th>
            <th class="col">Email</th>
            <th class="col">Date created</th>
            <th class="col"></th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let item of Users">
            <td>{{item.userName}}</td>
            <td class="td-style">
                <span class="status" [class]="item.isActivated?'active':'notActive'">
                    {{item.isActivated?'Active':'Not Active'}}
                </span></td>
            <td>{{item.phoneNumber}}</td>
            <td>{{item.email}}</td>
            <td>{{item.creationDate |date:'MMM d, y, h:mm:ss a'}}</td>
            <td><button mat-icon-button [matMenuTriggerFor]="menu" aria-label="Example icon-button with a menu">
                <mat-icon>more_vert</mat-icon>
              </button>
              <mat-menu #menu="matMenu">
                <button mat-menu-item (click)="openDialog(item)">
                    <i class="fa-solid fa-ban p-1"></i>
                  <span>{{item.isActivated?'Block':'unBlock'}}</span>
                </button>
                <button mat-menu-item>
                    <i class="fa-solid fa-eye p-1"></i>
                  <span>View</span>
                </button>
              </mat-menu></td>
        </tr>
    </tbody>

</table>
</div>
<mat-paginator [length]="length"
[pageSize]="pageSize"
[pageIndex]="pageIndex"
[pageSizeOptions]="pageSizeOptions"
(page)="handlePageEvent($event)"
aria-label="Select page">
</mat-paginator>
